       </div>

       <div>
        <p>{post.description}</p>
       </div>

       <div>
        <img src={post.image}/>
       </div>

       <div>
        <p>{post.price}</p>
       </div>

       <button>
          {
             selected ? (<p>Remove From Cart</p>) : (<p>Add to Cart</p>)
          }
       </button>
    </div>
  )
};

export default Product;
