import React, { useState } from "react";


const Product = ({post}) => {

   const {cart} = use


  return (
    <div>

       <div>
        <p>{post.title}</p>
       </div>

       <div>
        <p>{post.description}</p>
       </div>

       <div>
        <img src={post.image}/>
       </div>

       <div>
        <p>{post.price}</p>
       </div>

       <button>
          {
             Cart.some((p) => p.id == post.id ) ? (<p>Remove From Cart</p>) : (<p>Add to Cart</p>)
          }
       </button>
    </div>
  )
};

export default Product;
