import React from "react";
import { useDispatch } from "react-redux";
import { toast } from "react-hot-toast";
import { FaTrash } from "react-icons/fa"; // Import the specific icon from react-icons/fa
import { remove } from "../redux/Slices/CartSlice";

const CartItem = ({ item, itemIndex }) => {

  const dispatch = useDispatch();
   const removeFromCart = () => {
     dispatch(remove(item.id));
     toast.success("Item Removed");
  }


  return (
    <div className="border border-black flex mt-5 p-4 rounded-xl shadow-2xl items-center">
      <div className="flex gap-5">
        <div>
          <img className="h-[15rem]" src={item.image} alt="product" />
        </div>
        <div className="w-[30rem] h-full ">
          <h1 className="font-bold text-[1.2rem]">{item.title}</h1>
          <p>{item.description}</p> 
          <div className="flex justify-between p-5">
            <p className="font-bold">{item.price}</p>
            <div className="text-red-500 font text-[1.2rem]" onClick={removeFromCart}>
              <FaTrash /> 
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CartItem;
