import React from "react";

const Product = ({post}) => {
  return (
    <div>

       <div>
        <p>{post.title}</p>
       </div>

       <div>
        <p>{post.description}</p>
       </div>

       <div>
        <img src={post}/>
       </div>

       <div>
        <p>{post}</p>
       </div>

       <button>
          {
             selected ? (<p>Remove From Cart</p>) : (<p>Add to Cart</p>)
          }
       </button>
    </div>
  )
};

export default Product;
